<?php
 function render_block_core_file( $attributes, $content ) { $should_load_view_script = ! empty( $attributes['displayPreview'] ) && ! wp_script_is( 'wp-block-file-view' ); if ( $should_load_view_script ) { wp_enqueue_script( 'wp-block-file-view' ); } $pattern = '@<object.+(?<attribute>aria-label="(?<filename>[^"]+)?")@i'; $content = preg_replace_callback( $pattern, function ( $matches ) { $filename = ! empty( $matches['filename'] ) ? $matches['filename'] : ''; $has_filename = ! empty( $filename ) && 'PDF embed' !== $filename; $label = $has_filename ? sprintf( __( 'Embed of %s.' ), $filename ) : __( 'PDF embed' ); return str_replace( $matches['attribute'], sprintf( 'aria-label="%s"', $label ), $matches[0] ); }, $content ); return $content; } function register_block_core_file() { register_block_type_from_metadata( __DIR__ . '/file', array( 'render_callback' => 'render_block_core_file', ) ); } add_action( 'init', 'register_block_core_file' ); 